package pojo;

public class TeaInfo {
    private String name;
    private String avatar;
    private String intro;

    public TeaInfo() {
        this.name = "张军";
        this.avatar = "data:image/jpeg;base64,";
        this.intro = "1973年生，中共党员，工学硕士，副教授，从教二十多年，先后任信息工程学院专任教师、现代教育技术中心主任、党委组织部部长、学生党建工作领导小组办公室主任、江苏省高校信息化建设专家库成员。从教二十多年来，张军老师始终坚持党的教育方针，热爱教育事业，有着强烈的事业心、责任心和奉献精神，能够时刻以一个优秀教师的标准严格要求自己。";
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public String getAvatar() {
        return avatar;
    }

    public void setAvatar(String avatar) {
        this.avatar = avatar;
    }

    public String getIntro() {
        return intro;
    }

    public void setIntro(String intro) {
        this.intro = intro;
    }
}
