mapper.update:
-- noinspection SqlDialectInspectionForFile
        update collegeHistory
        set year=#{year},
            info=#{info}
        where id=#{id}

-- noinspection SqlDialectInspectionForFile
        update collegeInfo
        set title=#{title},
            intro=#{intro}
        where id=#{id}

mapper.delete:
-- noinspection SqlDialectInspectionForFile
        delete from collegeHistory
        where id=#{id}

-- noinspection SqlDialectInspectionForFile
        delete from collegeInfo
        where id=#{id}

mapper.update:
-- noinspection SqlDialectInspectionForFile
        update JAASTATUS
        set dqszj=#{dqszj},
            xn=#{xn},
            xq=#{xq}
        where xymc=#{xymc}
